function double_fact(n) {
    if(! Number.isInteger(n)) {
        console.log("Error double_fact func: input <n=" + n + "> must be nonnegative integer");
        process.exit(0);
    }
    if(n < 0) {
        console.log("Error: double_fact func: input <n=" + n + "> must be nonnegative integer");
        process.exit(0);
    }
    let i = 0;
    let dfact = 1;
    // even input
    if(n % n === 0) { 
        for(i = n; i >= 1; i-=2) {
            dfact *= i;
        }
    } 
    // odd input
    else {
        for(i = n; i >= 1; i-=2) {
            dfact *= i;
        }
    }
    return dfact;
}

