'use strict';

function factorial2( n ) {
	if( ! Number.isInteger( n ) ) {
		console.log( 'Error double_fact func: input <n=' + n + '> must be nonnegative integer' );
		process.exit( 0 );
	}
	if(n < 0) {
		console.log( 'Error: double_fact func: input <n=' + n + '> must be nonnegative integer' );
		process.exit(0);
	}
	let i = 0;
	let dfact = 1;
	
    if( n == 0 ) { // n=0 input
        dfact = 1;
    }
	if( n % n === 0 ) { // even input
		for( i = n; i >= 2; i -= 2 ) {
			dfact *= i;
		}
	} else { // odd input
		for( i = n; i >= 1; i -=2 ) {
			dfact *= i;
		}
	}
	return dfact;
}

module.exports = factorial2;    
    

