'use strict';

/**
* Evaluates the double factorial of `n`.
*
* @param {number} n - input value
* @returns {number} factorial2
*
* @example
* var v = factorial2( 3 );
* // returns 3.0
*
* @example
* var v = factorial2( 4 );
* // returns 8.0
*
* @example
* var v = factorial2( 5 );
* // returns 15.0
*
* @example
* var v = factorial2( 301 );
* // returns Infinity
*
*/
function factorial2( n ) {
	var dfact = 1;
	var i = 0;
	if ( !Number.isInteger( n ) ) {
		// Throw 'Error: improper argument -- must be nonnegative integer';
		return NaN;
	}
	if (n < 0) {
		// Throw 'Error: negative number -- must be nonnegative integer';
		return NaN;
	}
	if ( n === 0 ) { // n=0 input
		dfact = 1;
	}
	if ( n % n === 0 ) { // even input
		for ( i = n; i >= 2; i -= 2 ) {
			dfact *= i;
		}
	} else { // odd input
		for ( i = n; i >= 1; i -=2 ) {
			dfact *= i;
		}
	}
	return dfact;
}

module.exports = factorial2;
