#!/usr/bin/node

var test = require('tape');
bangbang = require('./main.js');

test('double factorial test', function (t) {
    t.plan(6);
    t.equal(typeof(bangbang), 'function');
    t.equal(bangbang(0), 1);
    t.equal(bangbang(2), 2);
    t.equal(bangbang(3), 3);
    t.equal(bangbang(4), 8);
    t.equal(bangbang(5), 15);
});


