'use strict';

var randu = require( '@stdlib/random/base/randu' );
var ceil = require( '@stdlib/math/base/special/ceil' );
var fallingFactorial = require( './../lib' );

var n;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = ( randu()*40.0 ) - 20.0;
	n = ceil( randu()*20.0 );
	console.log( 'fallingFactorial(%d,%d) = %d', x, n, fallingFactorial( x, n ) );
}
