#include "stdlib/math/base/special/gamma_lanczos_sum_expg_scaled.h"
#include "stdlib/constants/float64/pinf.h"

/**
* Calculates the Lanczos sum for the approximation of the gamma function (scaled by `exp(-g)`, where `g = 10.900511`.
*
* @param {number} x - input value
* @returns {number} Lanczos sum approximation
*
* @example
* var v = gammaLanczosSumExpGScaled( 4.0 );
* // returns ~0.018
*
* @example
* var v = gammaLanczosSumExpGScaled( -1.5 );
* // returns ~25.337
*
* @example
* var v = gammaLanczosSumExpGScaled( -0.5 );
* // returns ~-12.911
*
* @example
* var v = gammaLanczosSumExpGScaled( 0.5 );
* // returns ~1.772
*
* @example
* var v = gammaLanczosSumExpGScaled( 0.0 );
* // returns Infinity
*
* @example
* var v = gammaLanczosSumExpGScaled( NaN );
* // returns NaN
*/
double stdlib_base_gamma_lanczos_sum_expg_scaled( const double x ) {
	double ax;
	double s1;
	double s2;
	double x1;
	if ( x == 0.0 ) {
		return STDLIB_CONSTANT_FLOAT64_PINF;
	}
	if ( x < 0.0 ) {
		ax = -x;
	} else {
		ax = x;
	}
	if ( ax <= 1.0 ) {
		s1 = 709811.662581658 + (x * (679979.8474157227 + (x * (293136.7857211597 + (x * (74887.54032914672 + (x * (12555.290582413863 + (x * (1443.4299244417066 + (x * (115.24194596137347 + (x * (6.309239205732627 + (x * (0.22668404630224365 + (x * (0.004826466289237662 + (x * 0.00004624429436045379))))))))))))))))))); // eslint-disable-line max-len
		s2 = 0.0 + (x * (362880.0 + (x * (1026576.0 + (x * (1172700.0 + (x * (723680.0 + (x * (269325.0 + (x * (63273.0 + (x * (9450.0 + (x * (870.0 + (x * (45.0 + (x * 1.0))))))))))))))))))); // eslint-disable-line max-len
	} else {
		x1 = 1.0 / x;
		s1 = 0.00004624429436045379 + (x1 * (0.004826466289237662 + (x1 * (0.22668404630224365 + (x1 * (6.309239205732627 + (x1 * (115.24194596137347 + (x1 * (1443.4299244417066 + (x1 * (12555.290582413863 + (x1 * (74887.54032914672 + (x1 * (293136.7857211597 + (x1 * (679979.8474157227 + (x1 * 709811.662581658))))))))))))))))))); // eslint-disable-line max-len
		s2 = 1.0 + (x1 * (45.0 + (x1 * (870.0 + (x1 * (9450.0 + (x1 * (63273.0 + (x1 * (269325.0 + (x1 * (723680.0 + (x1 * (1172700.0 + (x1 * (1026576.0 + (x1 * (362880.0 + (x1 * 0.0))))))))))))))))))); // eslint-disable-line max-len
	}
	return s1 / s2;
}
