#include "stdlib/math/base/special/gamma_lanczos_sum.h"
#include "stdlib/constants/float64/pinf.h"

/**
* Calculates the Lanczos sum approximation.
*
* @param {number} x - input value
* @returns {number} Lanczos sum approximation
*
* @example
* var v = gammaLanczosSum( 4.0 );
* // returns ~950.366
*
* @example
* var v = gammaLanczosSum( -1.5 );
* // returns ~1373366.245
*
* @example
* var v = gammaLanczosSum( -0.5 );
* // returns ~-699841.735
*
* @example
* var v = gammaLanczosSum( 0.5 );
* // returns ~96074.186
*
* @example
* var v = gammaLanczosSum( 0.0 );
* // returns Infinity
*
* @example
* var v = gammaLanczosSum( NaN );
* // returns NaN
*/
double stdlib_base_gamma_lanczos_sum( const double x ) {
	double ax;
	double s1;
	double s2;
	double x1;
	if( x == 0.0 ) {
		return STDLIB_CONSTANT_FLOAT64_PINF;
	}
	if ( x < 0.0 ) {
		ax = -x;
	} else {
		ax = x;
	}
	if ( ax <= 1.0 ) {
		s1 = 38474670393.31777 + (x * (36857665043.51951 + (x * (15889202453.72942 + (x * (4059208354.298835 + (x * (680547661.1834733 + (x * (78239755.00312005 + (x * (6246580.776401795 + (x * (341986.3488721347 + (x * (12287.194511824551 + (x * (261.61404416416684 + (x * 2.5066282746310007))))))))))))))))))); // eslint-disable-line max-len
		s2 = 0.0 + (x * (362880.0 + (x * (1026576.0 + (x * (1172700.0 + (x * (723680.0 + (x * (269325.0 + (x * (63273.0 + (x * (9450.0 + (x * (870.0 + (x * (45.0 + (x * 1.0))))))))))))))))))); // eslint-disable-line max-len
	} else {
		x1 = 1.0 / x;
		s1 = 2.5066282746310007 + (x1 * (261.61404416416684 + (x1 * (12287.194511824551 + (x1 * (341986.3488721347 + (x1 * (6246580.776401795 + (x1 * (78239755.00312005 + (x1 * (680547661.1834733 + (x1 * (4059208354.298835 + (x1 * (15889202453.72942 + (x1 * (36857665043.51951 + (x1 * 38474670393.31777))))))))))))))))))); // eslint-disable-line max-len
		s2 = 1.0 + (x1 * (45.0 + (x1 * (870.0 + (x1 * (9450.0 + (x1 * (63273.0 + (x1 * (269325.0 + (x1 * (723680.0 + (x1 * (1172700.0 + (x1 * (1026576.0 + (x1 * (362880.0 + (x1 * 0.0))))))))))))))))))); // eslint-disable-line max-len
	}
	return s1 / s2;
}
