'use strict';

var randu = require( '@stdlib/random/base/randu' );
var gammainc = require( './../lib' );

var i;
var x;
var s;

for ( i = 0; i < 100; i++ ) {
	x = randu() * 10.0;
	s = randu() * 10.0;
	console.log( 'x: %d, \t s: %d, \t f(x,s): %d',
		x.toFixed( 4 ),
		s.toFixed( 4 ),
		gammainc( x, s ).toFixed( 4 )
	);
}
