'use strict';

// MODULES //

var cos = require( '@stdlib/math/base/special/cos' );


// MAIN //

/**
* Computes the half-value versed cosine.
*
* @param {number} x - input value
* @returns {number} half-value versed cosine (in radians)
*
* @example
* var v = havercos( 0.0 );
* // returns 0.0
*
* @example
* var v = havercos( Math.PI/2.0 );
* // returns 0.5
*
* @example
* var v = havercos( -Math.PI/6.0 );
* // returns ~0.9330
*
* @example
* var v = havercos( NaN );
* // returns NaN
*/
function havercos( x ) {
	return (1.0 + cos( x )) / 2.0;
} // end FUNCTION havercos()


// EXPORTS //

module.exports = havercos;
