"""Generate fixtures."""

import os
import json
import numpy as np
from scipy.special import hyp2f1

# Get the file path:
FILE = os.path.realpath(__file__)

# Extract the directory in which this file resides:
DIR = os.path.dirname(FILE)


def gen(a, b, c, x, name):
    """Generate fixture data and write to file.

    # Arguments

    * `a`: domain
    * `b`: domain
    * `c`: domain
    * `x`: domain
    * `name::str`: output filename

    # Examples

    ``` python
    python> x = linspace(-1000, 1000, 2001)
    python> gen(x, './data.json')
    ```
    """
    y = hyp2f1(a, b, c, x)
    data = {
        "a": a.tolist(),
        "b": b.tolist(),
        "c": c.tolist(),
        "x": x.tolist(),
        "expected": y.tolist()
    }

    # Based on the script directory, create an output filepath:
    filepath = os.path.join(DIR, name)

    # Write the data to the output filepath as JSON:
    with open(filepath, "w") as outfile:
        json.dump(data, outfile)


def main():

    """Generate fixture data."""
    # Basic values:
    n = 1000
    a = np.ones(n)
    b = np.ones(n)
    c = np.ones(n)
    x = np.linspace(-1.0, 0.99, n)
    gen(a, b, c, x, "basic1.json")

    # Basic values:
    n = 1000
    a = 2*np.ones(n)
    b = np.ones(n )
    c = np.ones(n)
    x = np.linspace(-1.0, 0.99, n)
    gen(a, b, c, x, "basic2.json")   

    # Basic values:
    n = 1000
    a = np.ones(n)
    b = 2*np.ones(n)
    c = np.ones(n)
    x = np.linspace(-1.0, 0.99, n)
    gen(a, b, c, x, "basic3.json")

    # Basic values:
    n = 1000
    a = 2*np.ones(n)
    b = 7*np.ones(n)
    c = 3*np.ones(n)
    x = np.linspace(-1.0, 0.99, n)
    gen(a, b, c, x, "basic4.json")    
       
if __name__ == "__main__":
    main()
