'use strict';

var randu = require( '@stdlib/random/base/randu' );
var kernelBetaincinv = require( './../lib' );

var i;
var p;
var a;
var b;

for ( i = 0; i < 100; i++ ) {
	p = randu();
	a = randu() * 10.0;
	b = randu() * 10.0;
	console.log( 'p: %d, \t a: %d, \t b: %d, \t f(p,a,b): %d',
		p.toFixed( 4 ),
		a.toFixed( 4 ),
		b.toFixed( 4 ),
		kernelBetaincinv( a, b, p, 1.0-p )[ 0 ]
	);
}
