// TypeScript Version: 4.1

/**
* Computes `log(x) - (x-1)` for `x` in `~[sqrt(2)/2, sqrt(2)]`.
*
* @param x - input value
* @returns function value
*
* @example
* var v = kernelLog( 1.0 );
* // returns 0.0
*
* @example
* var sqrt = require( '@stdlib/math/base/special/sqrt' );
*
* var v = kernelLog( sqrt( 2.0 ) );
* // returns ~-0.0676
*
* @example
* var v = kernelLog( NaN );
* // returns NaN
*/
declare function kernelLog( x: number ): number;


// EXPORTS //

export = kernelLog;
