'use strict';

/**
* Compute `log(x) - (x-1)` for `x` in `~[sqrt(2)/2, sqrt(2)]`.
*
* @module @stdlib/math/base/special/kernel-log
*
* @example
* var sqrt = require( '@stdlib/math/base/special/sqrt' );
* var kernelLog = require( '@stdlib/math/base/special/kernel-log' );
*
* var v = kernelLog( 1.0 );
* // returns 0.0
*
* v = kernelLog( sqrt( 2.0 ) );
* // returns ~-0.0676
*
* v = kernelLog( NaN );
* // returns NaN
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
