'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var lcm = require( './../lib' );

var a;
var b;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	a = round( randu()*50 );
	b = round( randu()*50 );
	v = lcm( a, b );
	console.log( 'lcm(%d,%d) = %d', a, b, v );
}
