'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var log = require( './../lib' );

var b;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = round( randu() * 100.0 );
	b = round( randu() * 5.0 );
	console.log( 'log( %d, %d ) = %d', x, b, log( x, b ) );
}
