'use strict';

/**
* Evaluate the common logarithm (base ten).
*
* @module @stdlib/math/base/special/log10
*
* @example
* var log10 = require( '@stdlib/math/base/special/log10' );
*
* var v = log10( 100.0 );
* // returns 2.0
*
* v = log10( 8.0 );
* // returns ~0.903
*
* v = log10( 0.0 );
* // returns Number.NEGATIVE_INFINITY
*
* v = log10( Number.POSITIVE_INFINITY );
* // returns Number.POSITIVE_INFINITY
*
* v = log10( NaN );
* // returns NaN
*
* v = log10( -4.0 );
* // returns NaN
*/

// MODULES //

var log10 = require( './log10.js' );


// EXPORTS //

module.exports = log10;
