'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var incrspace = require( '@stdlib/math/utils/incrspace' );
var abs = require( '@stdlib/math/base/special/abs' );
var exp = require( '@stdlib/math/base/special/exp' );
var EPS = require( '@stdlib/constants/math/float64-eps' );
var log1pexp = require( './../lib' );


// FIXTURES //

var expected = require( './fixtures/r/expected.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof log1pexp, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN`, the function returns `NaN`', function test( t ) {
	var v = log1pexp( NaN );
	t.strictEqual( isnan( v ), true, 'returns NaN when provided a NaN' );
	t.end();
});

tape( 'if `x <= -37`, the function returns `exp(x)`', function test( t ) {
	var values;
	var x;
	var y;
	var i;

	values = incrspace( -100.0, -37.0, 0.2 );
	for ( i = 0; i < values.length; i++ ) {
		x = log1pexp( values[ i ] );
		y = exp( values[ i ] );
		t.strictEqual( x, y, 'returns '+y+' when provided ' + values[ i ] );
	}
	t.end();
});

tape( 'if `x > 33.3`, the function returns `x`', function test( t ) {
	var values;
	var x;
	var y;
	var i;

	values = incrspace( 33.4, 100, 0.2 );
	for ( i = 0; i < values.length; i++ ) {
		x = log1pexp( values[ i ] );
		y = values[ i ];
		t.strictEqual( x, y, 'returns '+y+' when provided ' + values[ i ] );
	}
	t.end();
});

tape( 'the function accurately computes the natural logarithm of 1 + exp(x)', function test( t ) {
	var values;
	var delta;
	var tol;
	var x;
	var y;
	var i;

	values = incrspace( -100.0, 100.0, 0.1 );
	for ( i = 0; i < values.length; i++ ) {
		x = log1pexp( values[ i ] );
		y = expected[ i ];
		if ( x === expected[ i ] ) {
			t.strictEqual( y, expected[ i ], 'returns '+x+' when provided ' + values[ i ]);
		} else {
			delta = abs( x - y );
			tol = 20.9 * EPS * abs( y );
			t.ok( delta <= tol, 'within tolerance. returns '+x+' when provided '+values[ i ]+'. expected: '+expected[ i ]+', Î”: '+delta+'.' );
		}
	}
	t.end();
});
