// MODULES //

var getHighWord = require( '@stdlib/math/base/utils/float64-get-high-word' );
var setHighWord = require( '@stdlib/math/base/utils/float64-set-high-word' );
var setLowWord = require( '@stdlib/math/base/utils/float64-set-low-word' );
var toWords = require( '@stdlib/math/base/utils/float64-to-words' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var klog = require( './klog.js' );


// CONSTANTS //

var TWO54 =  1.80143985094819840000e+16; /* 0x43500000, 0x00000000 */
var IVLN2HI =  1.44269504072144627571e+00; /* 0x3ff71547, 0x65200000 */
var IVLN2LO =  1.67517131648865118353e-10; /* 0x3de705fc, 0x2eefa200 */


// LOG2 //

/**
* Evaluates the binary logarithm (base two).
*
* @param {number} x - input value
* @returns {number} function value
*
* @example
* var v = log2( 4.0 );
* // returns 2.0
* @example
* var v = log2( 8.0 );
* // returns 3.0
* @example
* var v = log2( 0.0 );
* // returns Number.NEGATIVE_INFINITY
* @example
* var v = log2( Number.POSITIVE_INFINITY );
* // returns Number.POSITIVE_INFINITY
* @example
* var v = log2( NaN );
* // returns NaN
* @example
* var v = log2( -4.0 );
* // returns NaN
*/
function log2( x ) {
	var words;
	var hi;
	var lo;
	var hx;
	var lx;
	var f;
	var i;
	var k;

	if ( x < 0.0 ) {
		return NaN;
	}

	words = toWords( x );
	hx = words[ 0 ];
	lx = words[ 1 ];

	k = 0;
	if ( hx < 0x00100000 ) {
		// Case: x < 2**-1022
		if ( ( (hx&0x7fffffff) | lx ) === 0 ) {
			return NINF;
		}
		k -= 54;
		// Subnormal number, scale up x:
		x *= TWO54;
		hx = getHighWord( x );
	}
	if ( hx >= 0x7ff00000 ) {
		return x+x;
	}
	k += (hx>>20) - 1023;
	hx &= 0x000fffff;
	i = ( hx+0x95f64 ) & 0x100000;
	// Normalize x or x/2...
	x = setHighWord( x, hx|(i^0x3ff00000 ) );
	k += (i>>20);
	f = klog( x );
	hi = x = x - 1;
	hi = setLowWord( hi, 0 );
	lo = x - hi;
	return (x+f) * IVLN2LO + (lo+f) * IVLN2HI + hi * IVLN2HI + k;
} // end FUNCTION log2()


// EXPORTS //

module.exports = log2;
