'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var abs = require( '@stdlib/math/base/special/abs' );
var PINF = require( '@stdlib/constants/math/float64-pinf' );
var NINF = require( '@stdlib/constants/math/float64-ninf' );
var EPS = require( '@stdlib/constants/math/float64-eps' );
var randu = require( '@stdlib/random/base/randu' );
var logaddexp = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/python/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof logaddexp, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `NaN` if provided `NaN`', function test( t ) {
	var v;

	v = logaddexp( NaN, 1 );
	t.equal( isnan( v ), true, 'returns NaN' );
	v = logaddexp( 1, NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `+infinity` if provided with `+infinity`', function test( t ) {
	var iterations;
	var x;
	var v;
	var i;

	iterations = 1000;
	for ( i = 0; i < iterations; i++ ) {
		x = ( randu() * 1000.0 ) - 500.0;
		v = logaddexp( PINF, x );
		t.strictEqual( v, PINF, 'returns +infinity when provided +infinity and '+x+'.' );
		v = logaddexp( x, PINF );
		t.strictEqual( v, PINF, 'returns +infinity when provided +infinity and '+x+'.' );
	}
	t.end();
});

tape( 'the function returns `x` if provided `-infinity` and `x`', function test( t ) {
	var iterations;
	var x;
	var v;
	var i;

	iterations = 1000;
	for ( i = 0; i < iterations; i++ ) {
		x = ( randu() * 1000.0 ) - 500.0;
		v = logaddexp( NINF, x );
		t.strictEqual( v, x, 'returns '+x+' when provided -infinity and '+x+'.' );
		v = logaddexp( x, NINF );
		t.strictEqual( v, x, 'returns '+x+' when provided -infinity and '+x+'.' );
	}
	t.end();
});

tape( 'the function accurately computes the natural logarithm of exp(x) + exp(y)', function test( t ) {
	var delta;
	var tol;
	var ex;
	var i;
	var v;
	var y;
	var x;

	for ( i = 0; i < data.length; i++ ) {
		x = data[i].input[0];
		y = data[i].input[1];
		ex = data[i].expected;
		v = logaddexp( x, y );
		if ( v === ex ) {
			t.strictEqual( v, ex, 'returns '+v+' when provided '+x+' and '+y+'.' );
		} else {
			delta = abs( ex - v );
			tol = EPS * abs( ex );
			t.ok( delta <= tol, 'within tolerance. returns '+v+' when provided '+x+' and '+y+'. expected: '+ex+', Î”: '+delta+'. tol: '+tol);
		}
	}
	t.end();
});
