'use strict';

/**
* Evaluate the logit function.
*
* @module @stdlib/math/base/special/logit
*
* @example
* var logit = require( '@stdlib/math/base/special/logit' );
*
* var y = logit( 0.2 );
* // returns ~-1.386
*
* y = logit( 0.9 );
* // returns ~2.197
*
* y = logit( -4.0 );
* // returns NaN
*
* y = logit( 1.5 );
* // returns NaN
*
* y = logit( NaN );
* // returns NaN
*/

// MODULES //

var logit = require( './logit.js' );


// EXPORTS //

module.exports = logit;
