#include "stdlib/math/base/special/minmax.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/assert/is_negative_zero.h"

/**
* Returns the minimum and maximum values.
*
* @param x - first number
* @param y - second number
* @return   minimum and maximum values
*
* @example
* stdlib_base_minmax( 3.14, NaN );
* // returns [ NaN, NaN ]
*
* @example
* stdlib_base_minmax( +0.0, -0.0 );
* // returns [ -0.0, 0.0 ]
*/
double stdlib_base_minmax( const double x, const double y, double* min, double* max ) {
    if ( stdlib_base_is_nan( x ) || stdlib_base_is_nan( y ) ) {
        *min = 0.0 / 0.0; // NaN
		*max = 0.0 / 0.0; // NaN
		return;
    }
    if( x == y && x == 0.0 ) {
        if ( stdlib_base_is_negative_zero ( x ) ) {
            *min = x;
	    *max = y;
	    return;
        }
        *min = y;
	*max = x;
	return;
    }
    if ( x < y ) {
        *min = x;
	*max = y;
	return;
    }
    *min = y;
    *max = x;
    return;
}
