#include "stdlib/math/base/special/minmaxabs.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/abs.h"

/**
* Returns the minimum and maximum absolute values.
*
* @param {number} x - first number
* @param {number} y - second number
* @returns {Array<number>}minimum and maximum absolute values.
*
* @example
* double v = stdlib_base_minmaxabs( 3.14, 4.2 );
* // returns [ 3.14, 4.2 ]
*
* @example
* double v = stdlib_base_minmaxabs( 3.14, NaN );
* // returns [ NaN, NaN ]
*
* @example
* double v = stdlib_base_minmaxabs( +0.0, -0.0 );
* // returns [ 0.0, 0.0 ]
*/
double stdlib_base_minmaxabs( const double x, const double y, double* minabs, double* maxabs ) {
    double ax;
    double ay;

    if ( stdlib_base_is_nan( x ) || stdlib_base_is_nan( y ) ) {
        *minabs = 0.0 / 0.0; // NaN
		*maxabs = 0.0 / 0.0; // NaN
		return;
    }
    ax = stdlib_base_abs( x );
    ay = stdlib_base_abs( y );
    if ( ax < ay ) {
        *minabs = ax;
		*maxabs = ay;
		return;
    }
    *minabs = ay;
	*maxabs = ax;
	return;
}
