"""Generate fixtures."""

import os
import json
import numpy as np
from scipy.special import eval_hermitenorm

# Get the file path:
FILE = os.path.realpath(__file__)

# Extract the directory in which this file resides:
DIR = os.path.dirname(FILE)


def gen(n, x, name):
    """Generate fixture data and write to file.

    # Arguments

    * `n`: degree
    * `x`: domain
    * `name::str`: output filename

    # Examples

    ``` python
    python> x = linspace(-1000, 1000, 2001)
    python> gen(1, x, './data.json')
    ```
    """
    y = eval_hermitenorm(n,x)
    data = {
        "n": n,
        "x": x.tolist(),
        "expected": y.tolist()
    }

    # Based on the script directory, create an output filepath:
    filepath = os.path.join(DIR, name)

    # Write the data to the output filepath as JSON:
    with open(filepath, "w") as outfile:
        json.dump(data, outfile)


def main():
    """Generate fixture data."""
    # Medium nagative values, n == 1:
    n = 1
    x = np.linspace(-709.78, -1.0, 1000)
    gen(n, x, "medium_negative_n_1.json")

    # Medium negative values, n == 2:
    n = 2
    x = np.linspace(-709.78, -1.0, 1000)
    gen(n, x, "medium_neagative_n_2.json")

    # Medium positive values, n == 1:
    n = 1
    x = np.linspace(1.0, 709.78, 1000)
    gen(n, x, "medium_positive_n_1.json")

    # Medium positive values, n == 2:
    n = 2
    x = np.linspace(2.0, 709.78, 1000)
    gen(n, x, "medium_positive_n_2.json")

    # Small positive values, n == 1:
    n = 1
    x = np.linspace(2.0**-54, 1.0, 1000)
    gen(n, x, "small_positive_n_1.json")

    # Small positive values, n == 2:
    n = 2
    x = np.linspace(2.0**-54, 1.0, 1000)
    gen(n, x, "small_positive_n_2.json")

    # Tiny values, n == 1:
    n = 1
    x = np.linspace(-2.0**-54, 2.0**-54, 1000)
    gen(n, x, "tiny_n_1.json")

    # Tiny values, n == 2:
    n = 2
    x = np.linspace(-2.0**-54, 2.0**-54, 1000)
    gen(n, x, "tiny_n_2.json")


if __name__ == "__main__":
    main()
