'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var polygamma = require( './../lib' );

var n;
var x;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	x = (randu()*100.0) - 50.0;
	n = round( randu()*50.0 );
	v = polygamma( n, x );
	console.log( 'x: %d, Ïˆ^(%d)(x): %d', x, n, v );
}
