'use strict';

var linspace = require( '@stdlib/math/utils/linspace' );
var rempio2 = require( './../lib' );

var x = linspace( 0.0, 100.0, 100 );
var y = new Array( 2 );
var n;
var i;

for ( i = 0; i < x.length; i++ ) {
	n = rempio2( x[ i ], y );
	console.log( '%d - pi/2 * %d = %d + %d', x[ i ], n, y[ 0 ], y[ 1 ] );
}
