'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var signum = require( './../lib' );

var sign;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = round( randu()*100.0 ) - 50.0;
	sign = signum( x );
	console.log( 'signum(%d) = %d', x, sign );
}
