'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Evaluates the signum function.
*
* @param {number} x - input value
* @returns {number} function value
*
* @example
* var sign = signum( -5.0 );
* // returns -1.0
* @example
* var sign = signum( 5.0 );
* // returns 1.0
* @example
* var sign = signum( -0.0 );
* // returns -0.0
* @example
* var sign = signum( 0.0 );
* // returns 0.0
* @example
* var sign = signum( NaN );
* // returns NaN
*/
function signum( x ) {
	if ( x === 0.0 || isnan( x ) ) {
		return x; // addresses both +-0
	}
	return ( x < 0.0 ) ? -1.0 : 1.0;
} // end FUNCTION signum()


// EXPORTS //

module.exports = signum;
