'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var isNegativeZero = require( '@stdlib/math/base/assert/is-negative-zero' );
var isPositiveZero = require( '@stdlib/math/base/assert/is-positive-zero' );
var PI = require( '@stdlib/math/constants/float64-pi' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var signum = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof signum, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `NaN` if provided `NaN`', function test( t ) {
	var sign = signum( NaN );
	t.equal( isnan( sign ), true, 'returns NaN' );
	t.end();
});

tape( 'function returns `-0` if provided `-0`', function test( t ) {
	var sign = signum( -0.0 );
	t.equal( isNegativeZero( sign ), true, 'returns -0' );
	t.end();
});

tape( 'function returns `0` if provided +0', function test( t ) {
	var sign;

	sign = signum( 0.0 );
	t.equal( isPositiveZero( sign ), true, 'returns +0' );

	sign = signum( +0.0 );
	t.equal( isPositiveZero( sign ), true, 'returns +0' );

	t.end();
});

tape( 'function returns `-1` if provided a negative number', function test( t ) {
	t.equal( signum( -10.0 ), -1.0, 'signum(-10) => -1' );
	t.equal( signum( -PI ), -1.0, 'signum(-Ï€) => -1' );
	t.equal( signum( NINF ), -1.0, 'signum(-infinity) => -1' );
	t.end();
});

tape( 'function returns `+1` if provided a positive number', function test( t ) {
	t.equal( signum( 10.0 ), 1.0, 'signum(10) => 1' );
	t.equal( signum( PI ), 1.0, 'signum(Ï€) => 1' );
	t.equal( signum( PINF ), 1.0, 'signum(infinity) => 1' );
	t.end();
});
