#!/usr/bin/env julia

import JSON

"""
    gen( x, name )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( -1000.0, 1000.0, 2001 );
julia> gen( x, \"data.json\" );
```
"""
function gen( x, name )
	y = sin( x );

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("expected", y)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Negative medium sized values:
x = linspace( -256.0*pi, 0.0, 4000 )
gen( x, "medium_negative.json" );

# Positive medium sized values:
x = linspace( 0.0, 256.0*pi, 4000 )
gen( x, "medium_positive.json" );

# Negative large values:
x = linspace( -2.0^20*(pi/2.0), -2.0^60*(pi/2.0), 4000 )
gen( x, "large_negative.json" );

# Positive large values:
x = linspace( 2.0^20*(pi/2.0), 2.0^60*(pi/2.0), 4000 )
gen( x, "large_positive.json" );

# Negative huge values:
x = linspace( -2.0^60*(pi/2.0), -2.0^1000*(pi/2.0), 4000 )
gen( x, "huge_negative.json" );

# Positive huge values:
x = linspace( 2.0^60*(pi/2.0), 2.0^1000*(pi/2.0), 4000 )
gen( x, "huge_positive.json" );
