'use strict';

// MODULES //

var computeSincospi = require( './sincospi.js' );


// MAIN //

/**
* Simultaneously computes the sine and cosine of a number times Ï€.
*
* @param {(Array|TypedArray|Object)} [out] - destination array
* @param {number} x - input value
* @returns {(Array|TypedArray|Object)} sine and cosine (in radians)
*
* @example
* var v = sincospi( 0.0 );
* // returns [ ~0.0, 1.0 ]
*
* @example
* var v = sincospi( 0.5 );
* // returns [ ~1.0, ~0.0 ]
*
* @example
* var v = sincospi( 0.1 );
* // returns [ ~0.309, ~0.951 ]
*
* @example
* var v = sincospi( NaN );
* // returns [ NaN, NaN ]
*
* @example
* var out = new Float64Array( 2 );
*
* var v = sincospi( out, 0.0 );
* // return <Float64Array>[ ~0.0, ~1.0 ]
*
* var bool = ( v === out );
* // returns true
*/
function sincospi( out, x ) {
	if ( arguments.length === 1 ) {
		return computeSincospi( new Array( 2 ), out );
	}
	return computeSincospi( out, x );
} // end FUNCTION sincospi()


// EXPORTS //

module.exports = sincospi;
