#!/usr/bin/env julia

import JSON

"""
    gen( x, filepath )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `filepath::AbstractString`: filepath of the output file

# Examples

``` julia
julia> x = linspace( -1000, 1000, 2001 );
julia> gen( x, \"./data.json\" );
```
"""
function gen( x, filepath )
	s = sinpi( x );
	c = cospi( x );
	data = Dict([
		("x", x),
		("sin", s),
		("cos", c)
	]);
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate fixture data for integer values:
x = linspace( -1000.0, 1000.0, 2001 );
out = joinpath( dir, "integers.json" );
gen( x, out );

# Generate fixture data for decimal values:
x = linspace( -100.0, 100.0, 2003 )
out = joinpath( dir, "decimals.json" );
gen( x, out );
