'use strict';

var linspace = require( '@stdlib/math/utils/linspace' );
var PI = require( '@stdlib/math/constants/float64-pi' );
var tan = require( './../lib' );

var x;
var i;

x = linspace( -PI/2.0, PI/2.0, 100 );

for ( i = 0; i < x.length; i++ ) {
	console.log( 'tan(%d) = %d', x[ i ], tan( x[ i ] ) );
}
