'use strict';

// MODULES //

var floor = require( '@stdlib/math/base/special/floor' );
var ceil = require( '@stdlib/math/base/special/ceil' );


// TRUNCATE //

/**
* Rounds a numeric value toward zero.
*
* @param {number} x - input value
* @returns {number} rounded value
*
* @example
* var v = truncate( -4.2 );
* // returns -4.0
* @example
* var v = truncate( 9.99999 );
* // returns 9.0
* @example
* var v = truncate( 0.0 );
* // returns 0.0
* @example
* var v = truncate( -0.0 );
* // returns -0.0
* @example
* var v = truncate( NaN );
* // returns NaN
* @example
* var v = truncate( Number.POSITIVE_INFINITY );
* // returns Number.POSITIVE_INFINITY
* @example
* var v = truncate( Number.NEGATIVE_INFINITY );
* // returns Number.NEGATIVE_INFINITY
*/
function truncate( x ) {
	if ( x < 0.0 ) {
		return ceil( x );
	}
	return floor( x );
} // end FUNCTION truncate()


// EXPORTS //

module.exports = truncate;
