'use strict';

var randu = require( '@stdlib/random/base/randu' );
var truncn = require( './../lib' );

var x;
var n;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	x = (randu()*100.0) - 50.0;
	n = truncn( randu()*5.0, 0 );
	v = truncn( x, -n );
	console.log( 'x: %d. Number of decimals: %d. Rounded: %d.', x, n, v );
}
