#include "stdlib/math/base/special/wrap.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/trunc.h"

/**
* Wraps a value on the half-open interval [min,max).
*
* @param v    input value
* @param min  minimum value
* @param max  maximum value
* @return	  wrapped value
*
* @example
* double v = stdlib_base_wrap( 3.14, 0.0, 5.0 );
* // returns 3.14
*/
double stdlib_base_wrap( const double v, const double min, const double max ) {
	double delta;
	double vc = v;
	if ( stdlib_base_is_nan( vc ) || stdlib_base_is_nan( min ) || stdlib_base_is_nan( max ) || max <= min ) {
		return 0.0 / 0.0; // NaN
	}
	// Normalize +-0 to +0...
	if ( vc == 0.0 ) {
		vc = 0.0;
	}
	if ( min == 0.0 ) {
		min = 0.0;
	}
	if ( max == 0.0 ) {
		max = 0.0;
	}
	// Simple case where value is already within range...
	if ( min <= vc && vc < max ) {
		return vc;
	}
	// Perform range reduction...
	delta = max - min;
	if ( vc < min ) {
		vc += delta * ( stdlib_base_trunc( (min-vc)/delta ) + 1.0 );
	}
	return min + ( ( vc - min ) % delta );
}
