'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var xlog1py = require( './../lib' );

var x;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	x = randu();
	if ( x < 0.5 ) {
		x = 0.0;
	}
	y = ( randu() * 20.0 ) - 5.0;
	console.log( 'xlog1py(%d, %d) = %d', x, y, xlog1py( x, y ) );
}
