'use strict';

var hasGeneratorsSupport = require( '@stdlib/utils/detect-generator-support' )();
var continuedFraction = require( './../lib' );

var generator;
var out;


function createGenerator() {
	var str =  'generator = function* generator() {';
	str += 'while ( true ) {';
	str += 'yield [ 1, 1 ];';
	str += '}';
	str += '}';
	eval( str );
}

// GOLDEN RATIO //

if ( hasGeneratorsSupport ) {
	createGenerator();
	out = continuedFraction( generator(), {
		'keep': true
	});
	console.log( 'Golden ratio (generator): %d,', out );
}

out = continuedFraction( closure(), {
	'keep': true
});
console.log( 'Golden ratio (closure): %d', out );

function closure() {
	var ones = [ 1, 1 ];
	return function() {
		return ones;
	};
}
