'use strict';

var randu = require( '@stdlib/random/base/randu' );
var absdiff = require( './../lib' );

var x;
var y;
var d;
var i;

for ( i = 0; i < 100; i++ ) {
	x = (randu()*1.0e4) - 1.0e2;
	y = (randu()*1.0e4) - 1.0e2;
	d = absdiff( x, y );
	console.log( 'x = %d. y = %d. |x-y| = %d.', x, y, d );
}
