'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var isEven = require( './../lib' );

var bool;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = round( randu()*100.0 );
	bool = isEven( x );
	console.log( '%d is %s', x, ( bool ) ? 'even' : 'not even' );
}
