'use strict';

/**
* Maximum half-precision floating-point number.
*
* @module @stdlib/math/constants/float16-max
* @type {number}
*
* @example
* var FLOAT16_MAX = require( '@stdlib/math/constants/float16-max' );
* // returns 65504.0
*/


// MAIN //

/**
* The maximum half-precision floating-point number is given by
*
* ``` tex
* 2^{15} (2 - 2^{-10})
* ```
*
* @constant
* @type {number}
* @default 65504.0
* @see [half-precision floating-point form]{@link https://en.wikipedia.org/wiki/Half-precision_floating-point_format}
*/
var FLOAT16_MAX = 65504.0;


// EXPORTS //

module.exports = FLOAT16_MAX;
