'use strict';

/**
* Smallest positive half-precision floating-point normal number.
*
* @module @stdlib/math/constants/float16-smallest-normal
* @type {number}
*
* @example
* var FLOAT16_SMALLEST_NORMAL = require( '@stdlib/math/constants/float16-smallest-normal' );
* // returns 6.103515625e-5
*/


// MAIN //

/**
* The smallest positive half-precision floating-point normal number has the value
*
* ``` tex
* \frac{1}{2^{15-1}}
* ```
*
* which corresponds to the bit sequence
*
* ``` binarystring
* 0 00001 0000000000
* ```
*
* @constant
* @type {number}
* @default 6.103515625e-5
* @see [Half-precision floating-point format]{@link https://en.wikipedia.org/wiki/Half-precision_floating-point_format}
*/
var FLOAT16_SMALLEST_NORMAL = 6.103515625e-5;


// EXPORTS //

module.exports = FLOAT16_SMALLEST_NORMAL;

