'use strict';

/**
* Smallest positive half-precision floating-point subnormal number.
*
* @module @stdlib/math/constants/float16-smallest-subnormal
* @type {number}
*
* @example
* var FLOAT16_SMALLEST_SUBNORMAL = require( '@stdlib/math/constants/float16-smallest-subnormal' );
* // returns 5.960464477539063e-8
*/


// MAIN //

/**
* The smallest positive half-precision floating-point subnormal number has the value
*
* ``` tex
* \frac{1}{2^{15-1} 2^{10}}
* ```
*
* which corresponds to the bit sequence
*
* ``` binarystring
* 0 00000 0000000001
* ```
*
* @constant
* @type {number}
* @default 5.960464477539063e-8
* @see [Half-precision floating-point format]{@link https://en.wikipedia.org/wiki/Half-precision_floating-point_format}
*/

var FLOAT16_SMALLEST_SUBNORMAL = 5.960464477539063e-8;


// EXPORTS //

module.exports = FLOAT16_SMALLEST_SUBNORMAL;

