'use strict';

var getHighWord = require( '@stdlib/math/base/utils/float64-get-high-word' );
var toBinaryString = require( '@stdlib/types/number/uint32/base/to-binary-string' );
var FLOAT64_HIGH_WORD_EXPONENT_MASK = require( './../lib' );

var out;
var hi;
var x;

x = 11.5;
hi = getHighWord( x );
console.log( 'Word: %s', toBinaryString( hi ) );

out = hi & FLOAT64_HIGH_WORD_EXPONENT_MASK;
console.log( 'Isolate exponent bits: %s', toBinaryString( out ) );

out = hi | FLOAT64_HIGH_WORD_EXPONENT_MASK;
console.log( 'Turn on exponent bits: %s', toBinaryString( out ) );
