'use strict';

// MODULES //

var tape = require( 'tape' );
var FLOAT64_MAX_SAFE_LUCAS = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof FLOAT64_MAX_SAFE_LUCAS, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is 7639424778862807', function test( t ) {
	t.strictEqual( FLOAT64_MAX_SAFE_LUCAS, 7639424778862807, 'returns expected value' );
	t.end();
});
