'use strict';

var FLOAT64_MAX_SAFE_NTH_LUCAS = require( './../lib' ); // eslint-disable-line id-length

var v;
var i;

function lucas( n ) {
	var a;
	var b;
	var c;
	var i;

	a = 2;
	if ( n === 0 ) {
		return a;
	}
	b = 1;
	for ( i = 2; i <= n; i++ ) {
		c = a + b;
		a = b;
		b = c;
	}
	return b;
}

for ( i = 0; i < 100; i++ ) {
	v = lucas( i );
	if ( i > FLOAT64_MAX_SAFE_NTH_LUCAS ) {
		console.log( 'Unsafe: %d', v );
	} else {
		console.log( 'Safe:   %d', v );
	}
}
