'use strict';

/**
* Size (in bytes) of a 16-bit signed integer.
*
* @module @stdlib/math/constants/int16-num-bytes
* @type {integer32}
*
* @example
* var INT16_NUM_BYTES = require( '@stdlib/math/constants/int16-num-bytes' );
* // returns 2
*/


// MAIN //

/**
* Size (in bytes) of a 16-bit signed integer.
*
* @constant
* @type {integer32}
* @default 2
*/
var INT16_NUM_BYTES = 2|0; // asm type annotation


// EXPORTS //

module.exports = INT16_NUM_BYTES;
