'use strict';

// MODULES //

var tape = require( 'tape' );
var pow = require( '@stdlib/math/base/special/pow' );
var INT32_MAX = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof INT32_MAX, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value equals 2**31 - 1', function test( t ) {
	t.equal( INT32_MAX, pow(2, 31) - 1, 'equals 2**31 - 1' );
	t.end();
});
