'use strict';

/**
* Size (in bytes) of a 32-bit signed integer.
*
* @module @stdlib/math/constants/int32-num-bytes
* @type {integer32}
*
* @example
* var INT32_NUM_BYTES = require( '@stdlib/math/constants/int32-num-bytes' );
* // returns 4
*/


// MAIN //

/**
* Size (in bytes) of a 32-bit signed integer.
*
* @constant
* @type {integer32}
* @default 4
*/
var INT32_NUM_BYTES = 4|0; // asm type annotation


// EXPORTS //

module.exports = INT32_NUM_BYTES;
