#!/usr/bin/env julia

import JSON

"""
    gen( x, name )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( -1000, 1000, 2001 );
julia> gen( x, \"data.json\" );
```
"""
function gen( x, name )
	y = asinh( x );

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("expected", y)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Small(er) values:
x = linspace( -0.8, 0.8, 503 );
gen( x, "smaller.json" );

# Negative small values:
x = linspace( -0.8, -1.0, 503 );
gen( x, "small_negative.json" );

# Positive small values:
x = linspace( 0.8, 1.0, 503 );
gen( x, "small_positive.json" );

# Negative medium values:
x = linspace( -1.0, -3.0, 503 );
gen( x, "medium_negative.json" );

# Positive medium values:
x = linspace( 1.0, 3.0, 503 );
gen( x, "medium_positive.json" );

# Large negative values:
x = linspace( -3.0, -28.0, 503 );
gen( x, "large_negative.json" );

# Large positive values:
x = linspace( 3.0, 28.0, 503 );
gen( x, "large_positive.json" );

# Larger negative values:
x = linspace( -28.0, -100.0, 503 );
gen( x, "larger_negative.json" );

# Larger positive values:
x = linspace( 28.0, 100.0, 503 );
gen( x, "larger_positive.json" );
