'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var hypot = require( './../lib' );

var x;
var y;
var h;
var i;

for ( i = 0; i < 100; i++ ) {
	x = round( randu()*100.0 ) - 50.0;
	y = round( randu()*100.0 ) - 50.0;
	h = hypot( x, y );
	console.log( 'hypot(%d,%d) = %d', x, y, h );
}
